import React, {  useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { RootState } from '../app/store';
//import {MyScriipt} from "../scripts/main.js"

//import { useDispatch } from 'react-redux';
import { Link } from  'react-router-dom'
import { getSubCatalogs } from '../slice/catalogSlice';

interface ICProps {
    catagoryId: number
  }

//import { useAppSelector  } from '../app/hooks';


const SubCatagorySlider = (props : ICProps) => {

    const  {catagories, isLoading, error} = useSelector((state: RootState) => state.reducer.catagoryReducer);
    console.log(catagories, error, isLoading);
    const dispatch = useDispatch();

    useEffect(() => {
        debugger;
        console.log('catagoryId')
       console.log(props.catagoryId);
        
       console.log('subCatagory')
       console.log(catagories);

       fetchData();


        async function fetchData() {
            // You can await here
            const response = await dispatch(getSubCatalogs(props.catagoryId));
            return response;
            // ...
          }  

        const script = document.createElement('script');
        script.innerHTML = "carousel_slider(); slick_slider();";
        script.async = true;
        document.body.appendChild(script);
        return () => {
            document.body.removeChild(script);
        }
         
         
         
      }, [props,dispatch]);
    return (
        <div className="product subcategory">
        <div className="container">
            <div className="row">
                <div className="col-12">
                    <div className="cat_overlap radius_all_5">
                        <div className="row align-items-center">
                            
                            <div className="col-lg-12 col-md-8">
                                <div className="cat_slider mt-4 mt-md-0 carousel_slider owl-carousel owl-theme nav_style5 owl-loaded owl-drag" data-loop="true" data-dots="false" data-nav="true" data-margin="30" data-responsive="{&quot;0&quot;:{&quot;items&quot;: &quot;1&quot;}, &quot;380&quot;:{&quot;items&quot;: &quot;2&quot;}, &quot;991&quot;:{&quot;items&quot;: &quot;3&quot;}, &quot;1199&quot;:{&quot;items&quot;: &quot;4&quot;}}">






                                    <div className="owl-stage-outer">
                                        <div className="owl-stage owl-stage-ext" >
										{
											catagories.map((sub) =>(
												<div className="owl-item owl-item-ext cloned" >
													<div className="item">
														<div className="categories_box">
														  <Link to="#">
																<i className="flaticon-dress"></i>
																<span>{sub.Name}</span>
															</Link>
															
															  
														</div>
													</div>
												</div>
											))
										}                                           
	
											
                                        </div>
                                    </div>
									<div className="owl-nav"><button type="button" role="presentation" className="owl-prev"><span className="lnr lnr-chevron-left"></span></button>
                                    <button type="button" role="presentation" className="owl-next"><span className="lnr lnr-chevron-right"></span></button></div><div className="owl-dots disabled"></div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

            </div>
        </div>
    </div>
     
    );
  }
  
  
  export default SubCatagorySlider;
